// GetWeatherIcon returns the appropriate weather icon for the given condition and time of Day
func GetWeatherIcon(code IconCode, timeOfDay TimeOfDay) template.HTML {
	if iconSet, ok := weatherIcons[code]; ok {
		if icon, exists := iconSet[timeOfDay]; exists {
			return template.HTML(icon)
		}
		// If specific time of Day version doesn't exist, return Day version
		return template.HTML(iconSet[Day])
	}
	// Return a default icon if the weather condition is not found
	return template.HTML(`<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="inline-block align-middle"><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="16" x2="12" y2="12"></line><line x1="12" y1="8" x2="12.01" y2="8"></line></svg>`)
}

// GetTimeOfDayIcon returns an SVG icon for a given time of Day
func GetTimeOfDayIcon(timeOfDay TimeOfDay) template.HTML {
	if icon, ok := timeOfDayIcons[timeOfDay]; ok {
		return template.HTML(icon)
	}
	return template.HTML(`<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="inline-block align-middle"><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="16" x2="12" y2="12"></line><line x1="12" y1="8" x2="12.01" y2="8"></line></svg>`)
}

// CalculateTimeOfDay determines the time of Day based on the note time and sun events
func CalculateTimeOfDay(noteTime time.Time, sunEvents suncalc.SunEventTimes) TimeOfDay {
	switch {
	case noteTime.Before(sunEvents.CivilDawn):
		return Night
	case noteTime.Before(sunEvents.Sunrise):
		return Dawn
	case noteTime.Before(sunEvents.Sunset):
		return Day
	case noteTime.Before(sunEvents.CivilDusk):
		return Dusk
	default:
		return Night
	}
}

// StringToTimeOfDay converts a string representation of a time of Day to a TimeOfDay value
func StringToTimeOfDay(s string) TimeOfDay {
	switch s {
	case "night":
		return Night
	case "dawn":
		return Dawn
	case "day":
		return Day
	case "dusk":
		return Dusk
	default:
		return Day
	}
}
